import { stats } from '../constants';
import styles from '../style';

const Stats = () => {
  return (
    <section className={`${styles.flexCenter} flex-row flex-wrap sm:mb-20 mb-6 justify-evenly`}>
      {stats.map((stat) => (

        <div key={stat.id}>
          <h1 className='font-poppins font-semibold ss:text-[23px] text-[30px] text-white ss:leading-[25px] leading-[53px] flex mx-4'>
            {stat.value}
            <span className='font-popins font-light text-gradient text-[15px] items-center'>{stat.title}</span>
            </h1>
          </div>

      ))}
    </section>
  )
}

export default Stats
